public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.print("I love pizza\n"); // \n make a new line
		System.out.println("It's really good!\n");  // print vs println

		System.out.println("I love pizza\n");
		System.out.println("It's really good!\n");
		
		// adding a tab "\t" (\ began escape sequence)
		System.out.println("\tI love pizza");
		System.out.println("It's really good!\n");
		
		// adding quotes "\""
		System.out.println("\"I love pizza\"");
		System.out.println("It's really good!\n");
		
		// adding backslash "\\""
		System.out.println("\\I love pizza\\");
		System.out.println("It's really good!\n");
		
		System.out.println(); // Type Sysout and CRTL+Space bar IDE will auto filled
		
		// Hold CRTL + '=/+' to zoom in or CRTL + '-/_' to 
	}

}
